#' Flexible histogram
#'
#' 
#' @param data .
#' @param var .
#' @param group .
#' @param flipAxes .
#' @param bins .
#' @param binWidthType .
#' @param binWidth .
#' @param binLowerLimit .
#' @param binOpacity .
#' @param line .
#' @param lineSize .
#' @param density .
#' @param densityOpacity .
#' @param densityLineSize .
#' @param width .
#' @param height .
#' @param title .
#' @param titleAlign .
#' @param titleFontSize .
#' @param titleFontFace .
#' @param subtitle .
#' @param subtitleAlign .
#' @param subtitleFontSize .
#' @param subtitleFontFace .
#' @param caption .
#' @param captionAlign .
#' @param captionFontSize .
#' @param captionFontFace .
#' @param xLabel .
#' @param xLabelAlign .
#' @param xLabelFontSize .
#' @param xLabelFontFace .
#' @param yLabel .
#' @param yLabelAlign .
#' @param yLabelFontSize .
#' @param yLabelFontFace .
#' @param titleType .
#' @param yAxisLabelFontSize .
#' @param yAxisLabelRotation .
#' @param yAxisRangeType .
#' @param yAxisRangeMin .
#' @param yAxisRangeMax .
#' @param xAxisLabelFontSize .
#' @param xAxisLabelRotation .
#' @param xAxisRangeType .
#' @param xAxisRangeMin .
#' @param xAxisRangeMax .
#' @param legendTitle .
#' @param legendTitleFontSize .
#' @param legendTitleFontFace .
#' @param legendLabelFontSize .
#' @param legendLabelFontFace .
#' @param legendKeyWidth .
#' @param legendKeyHeight .
#' @param legenPositionType .
#' @param legendPosition .
#' @param legendJustification .
#' @param legendPositionX .
#' @param legendPositionY .
#' @param legendDirection .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#' }
#'
#' @export
flexhist <- function(
    data,
    var,
    group = NULL,
    flipAxes = FALSE,
    bins = TRUE,
    binWidthType = "auto",
    binWidth = 0.5,
    binLowerLimit,
    binOpacity = 1,
    line = FALSE,
    lineSize = 0.5,
    density = FALSE,
    densityOpacity = 0.2,
    densityLineSize = 0.5,
    width = 500,
    height = 500,
    title = "",
    titleAlign = "center",
    titleFontSize = 16,
    titleFontFace = "plain",
    subtitle = "",
    subtitleAlign = "left",
    subtitleFontSize = 16,
    subtitleFontFace = "plain",
    caption = "",
    captionAlign = "right",
    captionFontSize = 12,
    captionFontFace = "plain",
    xLabel = "",
    xLabelAlign = "center",
    xLabelFontSize = 16,
    xLabelFontFace = "plain",
    yLabel = "",
    yLabelAlign = "center",
    yLabelFontSize = 16,
    yLabelFontFace = "plain",
    titleType = "title",
    yAxisLabelFontSize = 12,
    yAxisLabelRotation = 0,
    yAxisRangeType = "auto",
    yAxisRangeMin = 0,
    yAxisRangeMax = 10,
    xAxisLabelFontSize = 12,
    xAxisLabelRotation = 0,
    xAxisRangeType = "auto",
    xAxisRangeMin = 0,
    xAxisRangeMax = 10,
    legendTitle = "",
    legendTitleFontSize = 16,
    legendTitleFontFace = "plain",
    legendLabelFontSize = 16,
    legendLabelFontFace = "plain",
    legendKeyWidth = 0.6,
    legendKeyHeight = 0.6,
    legenPositionType = "outside",
    legendPosition = "right",
    legendJustification = "center",
    legendPositionX = 0.8,
    legendPositionY = 0.5,
    legendDirection = "vertical") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("flexhist requires jmvcore to be installed (restart may be required)")

    if ( ! missing(var)) var <- jmvcore::resolveQuo(jmvcore::enquo(var))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(var), var, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in group) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- flexhistOptions$new(
        var = var,
        group = group,
        flipAxes = flipAxes,
        bins = bins,
        binWidthType = binWidthType,
        binWidth = binWidth,
        binLowerLimit = binLowerLimit,
        binOpacity = binOpacity,
        line = line,
        lineSize = lineSize,
